mapper.select:
select   
	 from `conf_info_panel`

	 	and status !=-1

	 	and  `panel_name` LIKE concat(concat('%',#{panelName}),'%')


	 	and `pk_id` = #{panelId}


	 	and `menu_id` = #{menuId}

SELECT p.`pk_id`, p.`panel_name`, p.`menu_id`, p.`remark`, p.`status`, p.`create_user`, p.`create_time`, p.`update_user`, p.`update_time` FROM conf_info_panel p WHERE p.`status` = 1 AND p.menu_id IN ( SELECT cv.menu_id FROM priv_conf_visit cv INNER JOIN priv_map_role_visit rv ON rv.priv_visit_id = cv.priv_visit_id INNER JOIN priv_map_user_role ur ON ur.role_id = rv.role_id WHERE cv.`status` = 1 AND rv.`status` = 1 AND ur.`status` = 1
AND ur.user_id = #{userId}
AND cv.priv_visit_id = #{privVisitId}
)
AND p.pk_id =#{panelId}
AND p.panel_name LIKE concat(concat('%',#{panelName}),'%')

SELECT p.`pk_id`, p.`panel_name`, p.`menu_id`, p.`remark`, p.`status`, p.`create_user`, p.`create_time`, p.`update_user`, p.`update_time`, v.priv_visit_id FROM conf_info_panel p inner JOIN priv_conf_visit v ON v.menu_id = p.menu_id WHERE p.`status` = 1 AND v.`status` = 1
and priv_visit_id=#{privVisitId}
AND p.pk_id =#{panelId}
AND p.menu_id =#{menuId}
AND p.panel_name LIKE concat(concat('%',#{panelName}),'%')

SELECT p.`pk_id` 'panelId', p.`panel_name` 'panelName', p.`menu_id` 'menuId', p.`remark`, p.`status`, p.`create_user` 'createUser', p.`create_time` 'createTime', p.`update_user` 'updateUser', p.`update_time` 'updateTime', rp.pk_id 'rolePanelId' FROM conf_info_panel p INNER JOIN priv_map_role_panel rp ON rp.panel_id = p.pk_id WHERE p.`status` = 1 AND rp.`status` = 1 and rp.role_id=#{roleId}
and p.menu_id =#{menuId}
AND p.panel_name LIKE concat(concat('%',#{panelName}),'%')
